import { useState } from 'react'

const ListaPreguntas = require('../Preguntas/preguntas')

const ComponentePregunta = () => {

    let PreguntasRespondidas = []

    const [MostrarPreguntas , setMostrarPreguntas] = useState(false)
    let indicePregunta = Math.round(Math.random()*(ListaPreguntas.length-1))
    
    let PreguntasAgregadas = []
    let IndicesOriginales = []
    let NuevoOrdenPosibilidades = []
    let Pregunta
    let Respuesta
    for (let i = 0 ; i <= 3 ; i++) {
        do {
        Pregunta = Math.round(Math.random()*3)
        }
        while (PreguntasAgregadas.includes(Pregunta))

        NuevoOrdenPosibilidades.push(ListaPreguntas[indicePregunta].posibilidades[Pregunta])
        PreguntasAgregadas.push(Pregunta)
        IndicesOriginales.push(ListaPreguntas[indicePregunta].posibilidades.indexOf(Pregunta))
    }

    
    function Handleclick () {
            setMostrarPreguntas(true)
            
        }
        const confirmar = document.getElementsByName('confirmar')    
    console.log(confirmar[0])
    /* confirmar[0].addEventListener('click', (e)=> {
        
        e.preventDefault()
        PreguntasRespondidas.push({
            pregunta: ListaPreguntas[indicePregunta] ,
            respuesta: Respuesta
        })
        console.log(PreguntasRespondidas)
        }
    )
 */
    function HandleCheck (a) {
        
        Respuesta = NuevoOrdenPosibilidades[a]
    }
        
        return ( 
            <div>
               

            {MostrarPreguntas && < div className='ComponentePregunta' >
                <div>
                    {ListaPreguntas[indicePregunta].pregunta}
                        
                    
                    {ListaPreguntas[indicePregunta].lineas.map(linea=>(
                        <p key={ListaPreguntas[indicePregunta].lineas.indexOf(linea)} >{linea}</p>
                        
                    ))
                    }
                    
                </div>
                    <div>
                        
                        <form >
                            <p> <input type='radio' name='opcion' onChange={()=>HandleCheck(0)}/> {NuevoOrdenPosibilidades[0]} </p> 
                            <p> <input type='radio' name='opcion' onChange={()=>HandleCheck(1)}/> {NuevoOrdenPosibilidades[1]} </p> 
                            <p> <input type='radio' name='opcion' onChange={()=>HandleCheck(2)}/> {NuevoOrdenPosibilidades[2]} </p> 
                            <p> <input type='radio' name='opcion' onChange={()=>HandleCheck(3)}/> {NuevoOrdenPosibilidades[3]} </p> 
                            <button name='confirmar'  >Confirmar</button>
                        </form>
                    </div>
                    <div className='barraProgreso'>
                        <div className='b1'/><div className='b2'/><div className='b3'/><div className='b4'/><div className='b5'/>
                        <div className='b6'/><div className='b7'/><div className='b8'/><div className='b9'/><div className='b10'/>
                    </div>
                     
                </div>}
            </div>
        );
}
 
export default ComponentePregunta;
