import { useState } from 'react';


const ListaPreguntas = require('../Preguntas/preguntas')



export default function ComponentePregunta  ({PreguntasRespondidas})  {
    
    
        
    const [indicePregunta , setIndicePregunta] = useState(Math.round(Math.random()*(ListaPreguntas.length-1)))
    
    do {
        setIndicePregunta(Math.round(Math.random()*(ListaPreguntas.length-1)))
    }
    while (PreguntasRespondidas.includes(indicePregunta))

    let PreguntasAgregadas = []
    let IndicesOriginales = []
    let NuevoOrdenPosibilidades = []
    let Pregunta
    let Respuesta

    for (let i = 0 ; i <= 3 ; i++) {
        do {
        Pregunta = Math.round(Math.random()*3)
        }
        while (PreguntasAgregadas.includes(Pregunta))

        NuevoOrdenPosibilidades.push(ListaPreguntas[indicePregunta].posibilidades[Pregunta])
        PreguntasAgregadas.push(Pregunta)
        IndicesOriginales.push(ListaPreguntas[indicePregunta].posibilidades.indexOf(Pregunta))
    }

        const confirmar =s=>{
            s.preventDefault()
            
            if(Respuesta===undefined) {
                alert('Indique una respuesta antes de continuar')
                return
            }
            PreguntasRespondidas.push(Respuesta)
            console.log(PreguntasRespondidas)   
            //window.location.href = `http://localhost:3000/test/${parseInt(numero)+1}`; 
        }
    function HandleCheck (a) {
        Respuesta = {
            indicePregunta : indicePregunta,
            RtaElegida : NuevoOrdenPosibilidades[a],
            
        }
        
    }
    
        return ( 
            <div>
                

              < div className='ComponentePregunta' >
                <div>
                    {ListaPreguntas[indicePregunta].pregunta}
                        
                    
                    {ListaPreguntas[indicePregunta].lineas.map(linea=>(
                        <p key={ListaPreguntas[indicePregunta].lineas.indexOf(linea)} >{linea}</p>
                        
                    ))
                    }
                    
                </div>
                    <div>
                        <form onSubmit={confirmar}>
                            <p> <input type='radio' name='opcion' onClick={()=>HandleCheck(0)}/> {NuevoOrdenPosibilidades[0]} </p> 
                            <p> <input type='radio' name='opcion' onClick={()=>HandleCheck(1)}/> {NuevoOrdenPosibilidades[1]} </p> 
                            <p> <input type='radio' name='opcion' onClick={()=>HandleCheck(2)}/> {NuevoOrdenPosibilidades[2]} </p> 
                            <p> <input type='radio' name='opcion' onClick={()=>HandleCheck(3)}/> {NuevoOrdenPosibilidades[3]} </p> 
                            <button type='submit' >Confirmar</button>
                        </form>
                    </div>
                  {/*   <div className='barraProgreso'>
                        <div className='b1'/><div className='b2'/><div className='b3'/><div className='b4'/><div className='b5'/>
                        <div className='b6'/><div className='b7'/><div className='b8'/><div className='b9'/><div className='b10'/>
                    </div>
                */}
                </div> 
            </div>
        );
}
